import numpy as np
from PIL import Image


def textobinary(secret):
    bits = ''.join(format(i, '08b') for i in bytearray(secret, encoding='utf-8'))
    return bits


def Pixel_Secret_Insertion(raw_data, ptr):
    color = bin(raw_data)[2:]
    old = color  # troubleshooting lines
    color = color[:len(color) - 1]
    color = color + secret[ptr]
    print("original-> ", old, "Modified-> ", color)  # troubleshooting lines
    return np.uint8(int(color, 2))


